declare type MenuItem = {
    label: string;
    ariaLabel?: string;
};
declare type MenuItemCheckbox = {
    label: string;
    type: "checkbox";
};
declare type MenuItemRadio = {
    label: string;
    name: string;
    type: "radio";
};
declare type MenuItemSeparator = {
    type: "separator";
};
declare type MenuButton = MenuItem & {
    menu: Menu;
};
declare type Menu = Array<MenuItem | MenuItemCheckbox | MenuItemRadio | MenuItemSeparator | MenuButton>;
declare type MenuBar = Array<MenuButton>;
declare const menuBar: MenuBar;
export default menuBar;
